// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@chainlink/contracts/src/v0.8/vrf/VRFConsumerBase.sol';

contract ArtRaffle is Ownable, VRFConsumerBase {
    uint256[] public raffleNumbers;
    uint256 public selectedNumber;
    bool public raffleCompleted;

    // Chainlink VRF configuration for Ethereum Sepolia
    address constant linkAddress = 0x779877A7B0D9E8603169DdbD7836e478b4624789; // Ethereum Sepolia LINK token
    address constant vrfCoordinator = 0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625; // Ethereum Sepolia VRF Coordinator

    uint32 constant callbackGasLimit = 100000;
    uint16 constant requestConfirmations = 3;
    uint32 constant numWords = 1;

    event NumbersSubmitted(uint256[] numbers);
    event WinningNumberSelected(uint256 number);

    constructor() 
        Ownable(msg.sender)
        VRFConsumerBase(vrfCoordinator, linkAddress) 
    {}

    function submitNumbers(uint256[] calldata _numbers) external onlyOwner {
        require(_numbers.length > 0, "Must submit at least one number");
        require(!raffleCompleted, "Raffle already completed");

        raffleNumbers = _numbers;
        emit NumbersSubmitted(_numbers);
    }

    function drawWinner() external onlyOwner {
        require(raffleNumbers.length > 0, "No numbers submitted");
        require(!raffleCompleted, "Raffle already completed");
        require(IERC20(linkAddress).balanceOf(address(this)) >= 0.1 ether, "Not enough LINK");

        bytes32 keyHash = 0x474e34a077df58807dbe9c96d3c009b23b3c6d0cce433e59bbf5b34f823bc56c; // Ethereum Sepolia key hash
        uint64 subscriptionId = 0; // Use 0 for direct funding

        requestRandomness(
            keyHash,
            callbackGasLimit
        );
    }

    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        uint256 winningIndex = randomness % raffleNumbers.length;
        selectedNumber = raffleNumbers[winningIndex];
        raffleCompleted = true;

        emit WinningNumberSelected(selectedNumber);
    }

    function getSubmittedNumbers() external view returns (uint256[] memory) {
        return raffleNumbers;
    }

    function getSelectedNumber() external view returns (uint256) {
        require(raffleCompleted, "Raffle not completed");
        return selectedNumber;
    }
}

