pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ArtRaffleV2 is VRFConsumerBaseV2, Ownable {
    // VRF Configuration
    VRFCoordinatorV2Interface COORDINATOR;
    uint64 public s_subscriptionId;
    bytes32 public s_keyHash = 0x474e34a077df58807dbe9c96d3c009b23b3c6d0cce433e59bbf5b34f823bc56c;
    uint32 public callbackGasLimit = 50000;
    uint16 public requestConfirmations = 1;
    uint32 public numWords = 1;

    // Raffle state
    uint256[] public raffleNumbers;
    uint256 public selectedNumber;
    bool public raffleCompleted;

    // Events
    event NumbersSubmitted(uint256[] numbers);
    event RaffleDrawRequested(uint256 indexed requestId, address indexed drawer);
    event WinningNumberSelected(uint256 number);

    constructor(uint64 subscriptionId) VRFConsumerBaseV2(0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625) Ownable(msg.sender) {
        COORDINATOR = VRFCoordinatorV2Interface(0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625);
        s_subscriptionId = subscriptionId;
    }

    function submitNumbers(uint256[] calldata _numbers) external onlyOwner {
        require(_numbers.length > 0, "Must submit at least one number");
        require(!raffleCompleted, "Raffle already completed");

        raffleNumbers = _numbers;
        emit NumbersSubmitted(_numbers);
    }

    function drawWinner() external onlyOwner returns (uint256 requestId) {
        require(raffleNumbers.length > 0, "No numbers submitted");
        require(!raffleCompleted, "Raffle already completed");

        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );

        raffleCompleted = true; // Mark as in progress
        emit RaffleDrawRequested(requestId, msg.sender);
    }

    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        require(raffleCompleted, "Raffle not in progress");

        uint256 winningIndex = _randomWords[0] % raffleNumbers.length;
        selectedNumber = raffleNumbers[winningIndex];

        emit WinningNumberSelected(selectedNumber);
    }

    function getSubmittedNumbers() external view returns (uint256[] memory) {
        return raffleNumbers;
    }

    function getSelectedNumber() external view returns (uint256) {
        require(raffleCompleted && selectedNumber != 0, "Raffle not completed");
        return selectedNumber;
    }

    function isRaffleCompleted() external view returns (bool) {
        return raffleCompleted && selectedNumber != 0;
    }
}

